css:
.slide-comparison-grid {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
}

.slide-comparison-grid .slide-title {
  margin-bottom: 32px;
  color: {theme_primary};
}

.slide-comparison-grid .slide-body {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow-y: auto;
}

.slide-comparison-grid .comparison-grid-container {
  display: grid;
  gap: 24px;
  width: 100%;
  height: 100%;
}

.slide-comparison-grid .comparison-grid-container[data-sections="2"] {
  grid-template-columns: repeat(2, 1fr);
}

.slide-comparison-grid .comparison-grid-container[data-sections="3"] {
  grid-template-columns: repeat(3, 1fr);
}

.slide-comparison-grid .comparison-grid-container[data-sections="4"] {
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 1fr);
}
