css:
.slide-data-table {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  background-color: #FFFFFF;
  background-image: radial-gradient(circle at 1px 1px, #94a3b8 1px, transparent 0);
  background-size: 2rem 2rem;
}

.slide-data-table .slide-title {
  font-weight: 700;
  line-height: 1.2;
  margin-bottom: 32px;
  color: {theme_primary};
}

.slide-data-table .slide-body {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 24px;
  overflow-y: auto;
}

.slide-data-table .slide-body table {
  width: 100%;
  border-collapse: collapse;
  margin: 0;
}

.slide-data-table .slide-body .table-caption {
  font-size: 14px;
  color: #64748b;
  margin-top: 8px;
  text-align: center;
}
