css:
.slide-data-table {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
}

.slide-data-table .slide-title {
  margin-bottom: 32px;
  color: {theme_primary};
}

.slide-data-table .slide-body {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 24px;
  overflow-y: auto;
}

.slide-data-table .slide-body table {
  width: 100%;
  border-collapse: collapse;
  margin: 0;
}

.slide-data-table .slide-body .table-caption {
  font-size: 14px;
  color: #64748b;
  margin-top: 8px;
  text-align: center;
}
