css:
.slide-icon-row {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
}

.slide-icon-row .slide-title {
  margin-bottom: 24px;
  color: {theme_primary};
}

.slide-icon-row .slide-subtitle {
  margin-bottom: 32px;
  color: #64748b;
  font-size: 18px;
}

.slide-icon-row .slide-body {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
}

.slide-icon-row .icon-row-container {
  display: flex;
  flex-direction: row;
  gap: 48px;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  width: 100%;
}
