css:
.slide-linear-process {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
}

.slide-linear-process .slide-title {
  margin-bottom: 24px;
  color: {theme_primary};
}

.slide-linear-process .section-header {
  margin-bottom: 32px;
  color: #64748b;
  font-size: 20px;
  font-weight: 600;
}

.slide-linear-process .slide-body {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
}

.slide-linear-process .linear-process-container {
  display: flex;
  flex-direction: row;
  gap: 24px;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  width: 100%;
}
