css:
.slide-workflow-diagram {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  background-color: #FFFFFF;
  background-image: radial-gradient(circle at 1px 1px, #94a3b8 1px, transparent 0);
  background-size: 2rem 2rem;
}

.slide-workflow-diagram .slide-title {
  font-size: 48px;
  font-weight: 700;
  line-height: 1.2;
  margin-bottom: 24px;
  color: {theme_primary};
}

.slide-workflow-diagram .slide-subtitle {
  margin-bottom: 32px;
  color: #64748b;
  font-size: 20px;
  font-weight: 500;
  line-height: 1.5;
}

.slide-workflow-diagram .slide-body {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 24px;
  overflow-y: auto;
}
