css:
.slide-workflow-diagram {
  width: 100%;
  height: 100%;
  padding: 48px 64px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
}

.slide-workflow-diagram .slide-title {
  margin-bottom: 24px;
  color: {theme_primary};
}

.slide-workflow-diagram .slide-subtitle {
  margin-bottom: 32px;
  color: #64748b;
  font-size: 18px;
}

.slide-workflow-diagram .slide-body {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 24px;
  overflow-y: auto;
}
